const xml = $json.body || '';
const decode = (value) => value
  .replace(/&amp;/g, '&')
  .replace(/&quot;/g, '"')
  .replace(/&#39;/g, "'")
  .replace(/&nbsp;/g, ' ')
  .replace(/\s+/g, ' ')
  .trim();

const items = xml.match(/<item>[\s\S]*?<\/item>/gi) || [];
const rows = [];
for (const item of items) {
  const title = decode((item.match(/<title><!\[CDATA\[(.*?)\]\]><\/title>/i) || [])[1] || (item.match(/<title>(.*?)<\/title>/i) || [])[1] || '');
  const link = decode((item.match(/<link>(.*?)<\/link>/i) || [])[1] || '');
  const pubDate = decode((item.match(/<pubDate>(.*?)<\/pubDate>/i) || [])[1] || '');
  if (!title || !link) {
    continue;
  }
  rows.push({
    title,
    link,
    pub_date: pubDate || null,
    source: 'kpopofficial.com'
  });
}

if (!rows.length) {
  throw new Error('No RSS items parsed.');
}

const headers = ['title','link','pub_date','source'];
const lines = [headers.join(',')];
for (const row of rows) {
  lines.push(headers.map((key) => {
    const value = row[key];
    if (value === null || value === undefined) {
      return '';
    }
    const text = String(value).replace(/"/g, '""');
    return /[",\n]/.test(text) ? `"${text}"` : text;
  }).join(','));
}
const csv = lines.join('\n');
const contentBase64 = Buffer.from(csv, 'utf8').toString('base64');

return [{
  json: {
    rows: rows.length,
    content_base64: contentBase64
  }
}];

const content = $items('Build CSV')[0].json.content_base64;
const sha = $json.sha || null;
const message = `ðŸ¤– Automated data update - ${new Date().toISOString().slice(0, 16).replace('T', ' ')}`;
const payload = { message, content };
if (sha) {
  payload.sha = sha;
}
return [{ json: payload }];
